<?php
use PHPUnit\Framework\TestCase;

include 'sherlock-and-valid-string.php';

// # .\vendor\bin\phpunit --bootstrap vendor\autoload.php sherlock-and-valid-string_test.php

class isValidTest extends TestCase
{
    public function testCase(){
        $A = [
            'aabbcd',
            'aabbccddeefghi',
            'abcdefghhgfedecba',
            '',
            'xxxaabbccrry',
        ];
        $result = [
            'NO',
            'NO',
            'YES',
            'YES',
            'NO',
        ];

        for($i=0;$i<count($result);$i++){
            $test = isValid($A[$i]);
            $this->assertEquals($result[$i], $test);
        }
    }
}